mapper.select:
SELECT count(*)
  FROM ats_master m, ats_item i
 WHERE i.item2cd = '98'
   AND m.itemdcd = i.item4cd

   AND ITEMDCD=#{itemdcd}


   AND AUSER LIKE CONCAT('%',#{auser},'%')


   AND ASTATE=#{astate}


   AND ANO LIKE CONCAT('%',#{ano},'%')


   AND APLACE=#{aplace}


    AND ANM LIKE CONCAT('%',#{anm},'%')

SELECT m.ITEMDCD AS itemdcd, i.ITEM4NM AS item4nm, m.ANO AS ano, m.ACDID AS acdid, m.ANM AS anm, m.AMODELNO AS amodelno, m.AMODELNM AS amodelnm, c.CONM AS conm, m.APART AS apart, m.AUSER AS auser, m.MYEAR AS myear, s.STATENM AS statenm, m.IYEAR AS iyear, m.APLACE AS aplace FROM ats_master m, ats_item i, ats_state s, corpcode c WHERE i.item2cd = '98' AND m.itemdcd = i.item4cd AND m.astate=s.statecd AND m.acorpcd=concat(c.cocd, c.divcd)
AND ITEMDCD=#{itemdcd}
AND AUSER LIKE CONCAT('%',#{auser},'%')
AND ASTATE=#{astate}
AND ANO LIKE CONCAT('%',#{ano},'%')
AND APLACE=#{aplace}
AND ANM LIKE CONCAT('%',#{anm},'%')
ORDER BY ANO DESC LIMIT #{number}, 20

mapper.update:
UPDATE ATS_MASTER
   SET ITEMDCD=#{itemdcd}, ANM=#{anm}, ACORPCD=#{acorpcd}, APART=#{apart},AUSER=#{auser}, MYEAR=#{myear}, MCORP=#{mcorp},ASTATE=#{astate}, REGID=#{regid}, REGIP=#{regip}, REGDT=now(),
       ATTR1=#{attr1}, ATTR2=#{attr2}, ATTR3=#{attr3}, ATTR4=#{attr4}, ATTR5=#{attr5}, IYEAR=#{iyear}, APLACE=#{aplace}, SPEC=#{spec}, ACDID=#{acdid}, PRICE=#{price}
 WHERE ANO=#{ano}
